switch(data.applicationStatus) {
  case "New":
  case "Approved":
  case "Rejected":
    show = false;
    break;
  default: show = true;
}

const UserDetails = JSON.parse(localStorage.getItem("UserDetails"));
const tenantKey = UserDetails["tenantKey"]; // Get the tenantKey
const groupToCheck = `/${tenantKey}-formsflow-reviewer`; // Construct the group string
const groups = UserDetails["groups"];

if (groups.includes(groupToCheck) && data.applicationStatus === "New") {
  show = true;
} else {
  show = false;
}
